package ejerciciounolibro;

public class ejercicio1java {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		        int x = 144;
		        int y = 999;

		        System.out.println("Valor de x: " + x);
		        System.out.println("Valor de y: " + y);

		        int suma = x + y;
		        int resta = x - y;
		        int multiplicacion = x * y;
		        double division = (double) x / y; 

		        System.out.println("Suma (x + y): " + suma);
		        System.out.println("Resta (x - y): " + resta);
		        System.out.println("Multiplicación (x * y): " + multiplicacion);
		        System.out.println("División (x / y): " + division);
		    }
	


	}

